import java.util.*;class student{	static int noOfStudent = 0;	String name;	int rollNo;	char div;	void setStudent()	{			Scanner sc = new Scanner(System.in);		System.out.print("\nEnter the Name of the student : ");		name = sc.nextLine();		System.out.print("Enter the Roll no of the student : ");		rollNo = sc.nextInt();		System.out.print("Enter the division of the student : ");			div = sc.next().charAt(0);		noOfStudent++;	}	static	{		System.out.println("This is Static Block.\nThis will be exicuted in the beginning of the Program");	}	static void staticMethod()	{		System.out.println("This is static method.\nThis belongs to whole class and can be accessed without instance of the class");	}	int getNoOfStudents()	{		return noOfStudent;	}}class p8{	public static void main(String [] args)	{		student s1 = new student();		student s2 = new student();		s1.setStudent();							s2.setStudent();						int count = s1.getNoOfStudents();		System.out.println("\nTotal Students : " + count);		student.staticMethod();	}}